import React, { useEffect, useRef } from 'react';
import { Box, CircularProgress } from '@mui/material';

const WebView = () => {
    const iframeRef = useRef(null);
    const [loading, setLoading] = React.useState(true);

    useEffect(() => {
        // Function to inject scripts
        const injectScripts = () => {
            const iframe = iframeRef.current;
            if (!iframe || !iframe.contentWindow) return;

            try {
                // Add watermark
                const watermark = document.createElement('div');
                watermark.style.cssText = `
                    position: fixed;
                    bottom: 20px;
                    right: 20px;
                    background: rgba(0, 0, 0, 0.7);
                    color: white;
                    padding: 8px 15px;
                    border-radius: 8px;
                    z-index: 2147483646;
                    font-family: Arial, sans-serif;
                    font-size: 14px;
                `;
                watermark.innerHTML = 'Developed by Cursor';
                iframe.contentDocument.body.appendChild(watermark);

                // Monitor URL changes for WinGo page
                const observer = new MutationObserver(() => {
                    if (iframe.contentWindow.location.href.includes('/home/AllLotteryGames/WinGo?id=1')) {
                        injectWingoAnimation(iframe.contentDocument);
                    }
                });

                observer.observe(iframe.contentDocument, {
                    subtree: true,
                    childList: true
                });
            } catch (error) {
                console.error('Script injection failed:', error);
            }
        };

        const iframe = iframeRef.current;
        if (iframe) {
            iframe.addEventListener('load', () => {
                setLoading(false);
                injectScripts();
            });
        }

        return () => {
            if (iframe) {
                iframe.removeEventListener('load', injectScripts);
            }
        };
    }, []);

    return (
        <Box sx={{ width: '100%', height: '100vh', position: 'relative' }}>
            {loading && (
                <Box sx={{
                    position: 'absolute',
                    top: '50%',
                    left: '50%',
                    transform: 'translate(-50%, -50%)'
                }}>
                    <CircularProgress />
                </Box>
            )}
            <iframe
                ref={iframeRef}
                src="/proxy"
                style={{
                    width: '100%',
                    height: '100%',
                    border: 'none',
                    opacity: loading ? 0 : 1,
                    transition: 'opacity 0.3s ease'
                }}
            />
        </Box>
    );
};

export default WebView;
